import * as axios from 'axios';
import { format } from 'date-fns';
import { inputDateFormat } from './constants';
import { API } from './config';

const getHeros = async function() {
  try {
    const responce = axios.get(`${API} /heroes.Json`);
    let data = prelist(responce);
    const heroes = data.map(h => {
      h.originDate = format(h.originDate, inputDateFormat);
      return h;
    });
    return heroes;
  } catch (error) {
    console.error(error);
    return [];
  }
};
const prelist = responce => {
  if (responce.status != 200) throw Error(responce.message);
  if (!responce.data) return [];
  let list = responce.data;
  if (typeof list != 'object') {
    list = [];
  }
  return list;
};

export const data = {
  getHeros,
};
